function [m,p]=getLinkMassesAndCOMs()%Computation of link volumes, link assumed to have homogenous density and%all made by same material%Baseradious=0.025;height=0.4;v0=radious^2*pi*height;%Link 1-2height=0.4;base=0.03;width=0.03;v1=base*height*width;v2=v1;%Link 3height=0.4;base=0.025;width=0.025;v3=base*height*width;V=[v1 v2 v3];%Density of the link - Chosen arbitrarily, ro=8000 kg/m^3ro=8000;% Position of center of mass w.r.t. base framesyms d1 d2 d3TB1 = getTransformationMatrices().TB1;p1=[0 -0.2 0 1]';pl1=TB1*p1;TB2 = getTransformationMatrices().TB2;p2=[0 0.1498 0 1]';pl2=TB2*p2;TBE = getTransformationMatrices().TBE;p3=[0 0 -0.2 1]';pl3=TBE*p3;p=[pl1 pl2 pl3];p=p(1:3,:);m=V*ro;end